import axios from 'axios';

export const getBlogs = () => {
  return (dispatch) => {
    axios.get('/api/blogs')
    .then(res => dispatch({ type: 'BLOGS', blogs: res.data}))
    debugger
  }
}

export default ( state = [], action ) => {
  debugger
  switch(action.type) {
    case 'BlOGS':
    return action.blogs
    case 'ADD_BLOG':
      return [action.blog, ...state];
    case 'DELETE':
      return state.filter( blog => {
        return (blog.id !== action.id)
      })
    default:
      return state
  }
}
